import React from 'react';
import './Home.css'

const Home = () => {
  setTimeout(transition, 1000);

  $('.js-trigger-transition').on('click', function(e) {
    e.preventDefault();
    transition();
  });

  function transition() {
    var tl = new TimelineMax();

    tl.to(CSSRulePlugin.getRule('body:before'), 0.2, {cssRule: {top: '50%' }, ease: Power2.easeOut}, 'close')
    .to(CSSRulePlugin.getRule('body:after'), 0.2, {cssRule: {bottom: '50%' }, ease: Power2.easeOut}, 'close')
    .to($('.loader'), 0.2, {opacity: 1})
    .to(CSSRulePlugin.getRule('body:before'), 0.2, {cssRule: {top: '0%' }, ease: Power2.easeOut}, '+=1.5', 'open')
    .to(CSSRulePlugin.getRule('body:after'), 0.2, {cssRule: {bottom: '0%' }, ease: Power2.easeOut}, '-=0.2', 'open')
    .to($('.loader'), 0.2, {opacity: 0}, '-=0.2');
  }

  return (
    <div className='outerContainer'>
      <div className="loader">
        <div className="bar1"></div>
        <div className="bar2"></div>
        <div className="bar3"></div>
        <div className="bar4"></div>
        <div className="bar5"></div>
        <div className="bar6"></div>
      </div>
      <div>
        <a className='homeLink' href={'/join'}>
          <h1 className='homeH1'>WMChat <span role="img" aria-label="emoji"> ðŸ’¬</span></h1>
        </a>
      </div> 
    </div> 
  )

}

export default Home;
